// client > src > components >profile-component.js
import { useState, useEffect } from "react";
import AuthService from "../services/auth.service";

// 這個頁面就是要從props 裡面 拿到currentUser, setCurrentUser
const ProfileComponent = (props) => {
  // 我要知道目前登入的人是誰
  // 先假定目前來到這個profile-component 的時候current user 的state 是null
  let { currentUser, setCurrentUser } = useState(null);
  // 一進來這個頁面就要讓他執行useEffect 裡面的callback FN
  // 後面放一個empty array 代表說profile-component只要被渲染 就要讓他執行前面的 callbackFn
  useEffect(() => {
    setCurrentUser();
  }, []);
  return (
    <div style={{ padding: "3rem" }}>
      {!currentUser && <div>在獲取您的個人資料之前，您必須先登錄。</div>}
      {currentUser && (
        <div>
          <h2>以下是您的個人檔案：</h2>

          <table className="table">
            <tbody>
              <tr>
                <td>
                  <strong>姓名：{currentUser.user.username}</strong>
                </td>
              </tr>
              <tr>
                <td>
                  <strong>您的用戶ID: {currentUser.user._id}</strong>
                </td>
              </tr>
              <tr>
                <td>
                  <strong>您註冊的電子信箱: {currentUser.user.email}</strong>
                </td>
              </tr>
              <tr>
                <td>
                  <strong>身份: {currentUser.user.role}</strong>
                </td>
              </tr>
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default ProfileComponent;
