import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import course1 from "./img/course1.svg";
import course2 from "./img/course2.svg";
import courseService from "../services/course.service";
import authService from "../services/auth.service";

const CourseComponent = ({ currentUser, setCurrentUser }) => {
  const navigate = useNavigate();

  // ✅ 如果沒有登入，顯示請先登入畫面
  if (!currentUser) {
    return (
      <div style={{ padding: "3rem" }}>
        <p>⚠️ 您尚未登入，請先登入後再管理課程。</p>
        <button className="btn btn-primary" onClick={() => navigate("/login")}>
          前往登入頁面
        </button>
      </div>
    );
  }

  //導向修改課程畫面(教練操作)
  const handleUpdateCourse = (courseId) => {
    navigate(`/updateCourse/${courseId}`);
  };

  //教練可以刪除課程
  const handleDeleteCourse = (e) => {
    const courseId = e.target.id;
    if (window.confirm("您確定要刪除這個課程嗎？按下確定後即刪除課程!")) {
      courseService
        .deleteCourse(courseId)
        .then((response) => {
          window.alert(response.data.message);
          navigate("/");
        })
        .catch((error) => {
          window.alert(error.response.data.message || "刪除課程時發生錯誤。");
        });
    }
  };

  //導向添加課程畫面
  const handleAddCourse = () => {
    navigate("/allCourse");
  };

  //退出註冊的課程(學生操作)
  const handleQuit = (e) => {
    courseService
      .quit(e.target.id)
      .then(() => {
        window.alert("課程退出成功!!重新導向選課頁面。");
        setCurrentUser(authService.getCurrentUser());
        navigate("/allCourse");
      })
      .catch((e) => {
        console.log(e);
      });
  };

  const [courseData, setCourseData] = useState(null);

  useEffect(() => {
    let _id;
    if (currentUser) {
      _id = currentUser.user._id;
      if (currentUser.user.role === "instructor") {
        courseService
          .get(_id)
          .then((data) => {
            setCourseData(data.data);
          })
          .catch((e) => {
            console.log(e);
          });
      } else if (currentUser.user.role === "student") {
        courseService
          .getEnrolledCourses(_id)
          .then((data) => {
            setCourseData(data.data);
          })
          .catch((e) => {
            console.log(e);
          });
      }
    }
  }, []);

  return (
    <div style={{ padding: "3rem" }}>
      {currentUser.user.role === "instructor" && (
        <div>
          <div className="p-3 mb-3 bg-accent-custom rounded-3 shadow-bk">
            <div className="container-fluid">
              <div className="row align-items-center">
                <div className="col-md-8">
                  <h4 className="display-5 fw-bold">講師的課程頁面</h4>
                  <p>
                    老師您好，這是您目前在《上課趣》所開設的課程。
                    <br />
                    您可以進行課程修改、刪除課程，並確認目前學生人數。
                  </p>
                </div>
                <div className="col-md-4 d-flex justify-content-end py-5">
                  <img
                    src={course1}
                    alt="講師課程圖"
                    className="img-fluid"
                    style={{ maxHeight: "150px", width: "auto" }}
                  />
                </div>
              </div>
            </div>
          </div>
        </div>
      )}

      {currentUser.user.role === "instructor" &&
        courseData &&
        courseData.length !== 0 && (
          <div style={{ display: "flex", flexWrap: "wrap" }}>
            {courseData.map((course) => (
              <div
                key={course._id}
                className="card"
                style={{
                  width: "19.7rem",
                  flexWrap: "wrap",
                  padding: "0.5rem",
                  margin: "0.6rem",
                }}
              >
                <div className="card-body">
                  <h5 className="card-title">課程名稱: {course.title}</h5>
                  <p style={{ margin: "0.5rem 0rem" }} className="card-text">
                    {course.description}
                  </p>
                  <p style={{ margin: "0.5rem 0rem" }}>
                    學生人數: {course.students.length}
                  </p>
                  <p style={{ margin: "0.5rem 0rem" }}>
                    課程價格: {course.price}
                  </p>
                  <p style={{ margin: "0.5rem 0rem" }}>
                    課程日期: {course.courseDate}
                  </p>
                  <p style={{ margin: "0.5rem 0rem" }}>
                    課程時間: {course.courseTime}
                  </p>
                  <p style={{ margin: "0.5rem 0rem" }}>
                    課程講師: {course.instructor.username}
                  </p>
                </div>
                <button
                  className="btn btn-outline-secondary"
                  onClick={() => handleUpdateCourse(course._id)}
                >
                  修改課程內容
                </button>
                <br />
                <button
                  id={course._id}
                  className="btn btn-outline-danger"
                  onClick={handleDeleteCourse}
                >
                  刪除課程
                </button>
              </div>
            ))}
          </div>
        )}

      {currentUser.user.role === "student" && (
        <div>
          <div className="p-3 mb-3 bg-accent-custom rounded-3 shadow-bk">
            <div className="container-fluid py-4">
              <div className="row align-items-center">
                <div className="col-md-8">
                  <h4 className="display-5 fw-bold">學員的課程頁面</h4>
                  <p>
                    同學您好，以下是您目前已經註冊的課程。
                    <br />
                    想查閱更多課程並報名，請點選新增課程按鈕。
                  </p>
                  <button className="btn btn-dark" onClick={handleAddCourse}>
                    新增課程
                  </button>
                </div>
                <div className="col-md-4 d-flex justify-content-end">
                  <img
                    src={course2}
                    alt="學員課程圖"
                    className="img-fluid"
                    style={{ maxHeight: "150px", width: "auto" }}
                  />
                </div>
              </div>
            </div>
          </div>
        </div>
      )}

      {currentUser.user.role === "student" &&
        courseData &&
        courseData.length !== 0 && (
          <div style={{ display: "flex", flexWrap: "wrap" }}>
            {courseData.map((course) => (
              <div
                key={course._id}
                className="card"
                style={{
                  width: "19.7rem",
                  flexWrap: "wrap",
                  padding: "0.5rem",
                  margin: "0.6rem",
                }}
              >
                <div className="card-body">
                  <h5 className="card-title">課程名稱: {course.title}</h5>
                  <p style={{ margin: "0.5rem 0rem" }} className="card-text">
                    {course.description}
                  </p>
                  <p style={{ margin: "0.5rem 0rem" }}>
                    學生人數: {course.students.length}
                  </p>
                  <p style={{ margin: "0.5rem 0rem" }}>
                    課程價格: {course.price}
                  </p>
                  <p style={{ margin: "0.5rem 0rem" }}>
                    課程日期: {course.courseDate}
                  </p>
                  <p style={{ margin: "0.5rem 0rem" }}>
                    課程時間: {course.courseTime}
                  </p>
                </div>
                <button
                  id={course._id}
                  className="btn btn-outline-secondary btn-lg"
                  onClick={handleQuit}
                >
                  退出課程
                </button>
              </div>
            ))}
          </div>
        )}
    </div>
  );
};

export default CourseComponent;
