// 複貼chaosOK

import React from "react";
import { useNavigate } from "react-router-dom";
import homepage2 from "./img/homepage2.svg";
import homepage3 from "./img/homepage3.svg";
import homepage4 from "./img/homepage4.svg";
import homepage1 from "./img/homepage1.svg";
import homepage5 from "./img/homepage5.svg";
import homepage6 from "./img/homepage6.svg";
import banner1 from "./img/banner1.jpg";
import banner2 from "./img/banner2.jpg";
import banner3 from "./img/banner3.jpg";
import logo from "./img/logo.svg";
import "./style/style.css";

const HomeComponent = () => {
  const navigate = useNavigate();

  //導向所有課程
  const handleAddCourse = () => {
    window.alert("一起來看看有哪些課程吧!");
    navigate("/allCourse");
  };

  //導向張貼課程
  const handlePost = () => {
    window.alert("開始來上架新課程吧!");
    navigate("/postCourse");
  };

  //這是首頁
  return (
    <main>
      <div className="container py-4">
        <div className="p-5 mb-4 bg-primary-custom rounded-3 shadow-bk">

          <div className="container-fluid text-center">
            <br />

            <div className="card mx-auto w-100" style={{ maxWidth: "720px" }}>
              <div className="card-body">
                <h5 className="card-title">
                  使用者可依身份選擇登入為 <b>學員</b> 或 <b>講師</b>：
                </h5>
                <p className="card-text">
                  <i className="fa-solid fa-book-open-reader"></i> <b>學員</b>{" "}
                  可以瀏覽並預約下班後的各類興趣課程
                  <br />
                  <i className="fa-solid fa-chalkboard-user"></i> <b>講師</b>{" "}
                  可新增、編輯與刪除自己開設的課程內容
                </p>
              </div>
            </div>

            <hr />
            <p className="fw-bold fs-5">本網站為個人 Side Project</p>
            <p className="fs-5">
              前端採用 React 框架開發完成，後端使用 Node.js + Express.js。
            </p>
          </div>
        </div>
        {/* 四個圈圈s */}
        <div className="container-fluid p-5 text-center">
          <div className="col-md-12 d-flex justify-content-between flex-wrap gap-3 d-none d-md-flex">
            <div className="flex-grow-1 mx-2">
              <img src={homepage1} className="uniform-circle-img" alt="..." />
              <div className="card-body">
                <p className="card-text mt-4">下班不打烊，興趣開課中！</p>
              </div>
            </div>

            <div className="flex-grow-1 mx-2">
              <img src={homepage2} className="uniform-circle-img" alt="..." />
              <div className="card-body mt-4">
                <p className="card-text">累了嗎？選堂放鬆課吧！</p>
              </div>
            </div>

            <div className="flex-grow-1 mx-2">
              <img src={homepage3} className="uniform-circle-img" alt="..." />
              <div className="card-body">
                <p className="card-text mt-4">簡單充實下班時光</p>
              </div>
            </div>

            <div className="flex-grow-1 mx-2">
              <img src={homepage4} className="uniform-circle-img" alt="..." />
              <div className="card-body">
                <p className="card-text mt-4">給自己一點不一樣的晚上</p>
              </div>
            </div>
          </div>
        </div>
        {/* 四個圈圈e */}
        <div className="row align-items-md-stretch">
          <div className="col-md-6">
            <div className="bg-primary-custom p-5 text-dark bg-opacity-50 border rounded-3 shadow-bk">
              <div className="row align-items-center">
                {/* 左邊：文字與按鈕 */}
                <div className="col-md-8">
                  <h2>我是學員</h2>
                  <p>
                    無論是瑜伽、攝影、烘焙還是外語練習
                    <br />
                    一起在下班後開啟充實又快樂的學習時光！
                  </p>
                  <button
                    className="btn btn-light"
                    type="button"
                    onClick={handleAddCourse}
                  >
                    立即選課
                  </button>
                </div>

                {/* 右邊：圖片 */}
                <div className="col-md-4 d-flex justify-content-end">
                  <img
                    src={homepage6}
                    alt="學員圖片"
                    className="img-fluid uniform-circle-img"
                    style={{ maxHeight: "120px", width: "auto" }}
                  />
                </div>
              </div>
            </div>
          </div>

          <div className="col-md-6">
            <div className="h-100 p-5 bg-primary-custom bg-opacity-10 border rounded-3 shadow-bk">
              <div className="row align-items-center">
                {/* 左側：文字與按鈕 */}
                <div className="col-md-8">
                  <h2>我是講師</h2>
                  <p>
                    您可以在這裡發揮專業，上架課程！
                    <br />
                    推廣課程給更多人知道。
                  </p>
                  <button
                    className="btn btn-light"
                    type="button"
                    onClick={handlePost}
                  >
                    開設課程
                  </button>
                </div>

                {/* 右側：圖片 */}
                <div className="col-md-4 d-flex justify-content-end">
                  <img
                    src={homepage5}
                    alt="講師圖片"
                    className="img-fluid uniform-circle-img"
                    style={{ maxHeight: "120px", width: "auto" }}
                  />
                </div>
              </div>
            </div>
          </div>
        </div>

        <footer className="pt-3 mt-4 text-muted border-top ">
          &copy; 2025 WEI-YUAN CHANG Side Project
        </footer>
      </div>
    </main>
  );
};

export default HomeComponent;
