// 複貼chaosOK

import React from "react";
import { useNavigate } from "react-router-dom";
import homepage2 from "./img/homepage2.svg";
import homepage3 from "./img/homepage3.svg";
import homepage4 from "./img/homepage4.svg";
import homepage1 from "./img/homepage1.svg";
import homepage5 from "./img/homepage5.svg";
import homepage6 from "./img/homepage6.svg";
import "./style/style.css";

const HomeComponent = () => {
  const navigate = useNavigate();

  //導向所有課程
  const handleAddCourse = () => {
    window.alert("一起來看看有哪些課程吧!");
    navigate("/allCourse");
  };

  //導向張貼課程
  const handlePost = () => {
    window.alert("開始來上架新課程吧!");
    navigate("/postCourse");
  };

  //這是首頁
  return (
    <main>
      <div className="container py-4">
        <div className="p-5 mb-4 bg-primary-custom rounded-3">
          {/* 輪播區塊 */}
<div id="carouselExampleCaptions" className="carousel slide" data-bs-ride="carousel">
  {/* 輪播指示器 */}
  <div className="carousel-indicators">
    <button type="button" data-bs-target="#carouselExampleCaptions" data-bs-slide-to="0" className="active" aria-current="true" aria-label="Slide 1"></button>
    <button type="button" data-bs-target="#carouselExampleCaptions" data-bs-slide-to="1" aria-label="Slide 2"></button>
    <button type="button" data-bs-target="#carouselExampleCaptions" data-bs-slide-to="2" aria-label="Slide 3"></button>
  </div>

  {/* 輪播圖片與文字 */}
  <div className="carousel-inner">
    {[homepage1, homepage2, homepage3].map((imgSrc, index) => (
      <div
        key={index}
        className={`carousel-item position-relative ${index === 0 ? "active" : ""}`}
      >
        <img src={imgSrc} className="d-block w-100" alt={`Slide ${index + 1}`} />

        {/* 半透明遮罩 */}
        <div className="position-absolute top-0 start-0 w-100 h-100 bg-dark bg-opacity-50"></div>

        {/* 置中文字 */}
        <div className="carousel-caption d-none d-md-block position-absolute top-50 start-50 translate-middle text-white">
          <h5>{`第 ${index + 1} 張幻燈片`}</h5>
          <p>這裡可以放入課程簡介或網站介紹。</p>
        </div>
      </div>
    ))}
  </div>

  {/* 上/下一張按鈕 */}
  <button className="carousel-control-prev" type="button" data-bs-target="#carouselExampleCaptions" data-bs-slide="prev">
    <span className="carousel-control-prev-icon" aria-hidden="true"></span>
