//OK
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import AuthService from "../services/auth.service";
import login1 from "../components/img/login1.svg";

const LoginComponent = ({ currentUser, setCurrentUser }) => {
  const navigate = useNavigate();
  let [email, setEmail] = useState("");
  let [password, setPassword] = useState("");
  // 顯示錯誤訊息用
  let [message, setMessage] = useState("");

  const handleEmail = (e) => {
    setEmail(e.target.value);
  };
  const handlePassword = (e) => {
    setPassword(e.target.value);
  };

  //登入本地驗證
  const handleLogin = async () => {
    try {
      // 用email, password 這兩個state 做登入
      // AuthService.login(email, password)會return 一個promise
      let response = await AuthService.login(email, password);
      // 把從伺服器得到的response 存到localStorage
      // user 的key 對上JSON.stringify(response.data) data 物件 用localStorage 存起來
      localStorage.setItem("user", JSON.stringify(response.data));
      window.alert("登入成功！您現在將被重新導向到個人資料頁面。");
      // 在重新導向之前設定目前的使用者是誰
      // AuthService.getCurrentUser() 就是 localStorage.setItem("user", JSON.stringify(response.data)); 所設定的值
      // 系統就會有一個setCurrentUser 的值，他就會不斷追蹤目前我們的currentUser 是誰
      // 如有登入成功，就會設定setCurrentUser的值就要等於localStorage設定過的值

      setCurrentUser(AuthService.getCurrentUser());
      navigate("/profile");
    } catch (e) {
      setMessage(e.response.data);
    }
  };
  return (
    <div style={{ padding: "3rem" }} className="col-md-12">
      <div className="p-3 mb-3 bg-background-custom rounded-3">
        <div className="container-fluid py-4">
          <h4 className="display-5 fw-bold">
            <img
              src={login1}
              alt="good"
              style={{ height: "20%", width: "20%" }}
            ></img>
            登入下班上課趣
          </h4>
          <p className="col-md-8 fs-8">
            已經有學員/講師身分了嗎?
            <br />
            快點來登入，一起來上課吧。
          </p>
        </div>
      </div>
      <div className="col-md-12">
        <div>
          {message && <div className="alert alert-danger">{message}</div>}
          <div className="form-group">
            <label htmlFor="username">電子信箱：</label>
            <input
              onChange={handleEmail}
              type="text"
              className="form-control"
              name="email"
              placeholder="請輸入電子信箱"
            />
          </div>
          <br />
          <div className="form-group">
            <label htmlFor="password">密碼：</label>
            <input
              onChange={handlePassword}
              type="password"
              className="form-control"
              name="password"
              placeholder="密碼長度不得小於6字元"
            />
          </div>
          <br />
          <div className="form-group">
            <button onClick={handleLogin} className="btn btn-primary btn-block">
              <span>登入系統</span>
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LoginComponent;
