import React from "react";
import { Link } from "react-router-dom";
import { Navbar, Nav, Container } from "react-bootstrap";
import logo from "./img/logo.png";
import 'bootstrap/dist/css/bootstrap.min.css';

const NavComponent = ({ currentUser, setCurrentUser }) => {
  const handleLogout = () => {
    window.alert("登出成功!您會被導向到首頁。");
    setCurrentUser(null);
  };

  return (
    <Navbar expand="lg" bg="light" className="py-2">
      <Container>

        {/* 左側 Logo */}
        <Navbar.Brand as={Link} to="/" className="mx-auto mx-lg-0">
          <img src={logo} alt="Logo" style={{ height: "50px" }} />
        </Navbar.Brand>

        {/* 漢堡按鈕 */}
        <Navbar.Toggle aria-controls="basic-navbar-nav" />

        {/* 折疊選單 */}
        <Navbar.Collapse id="basic-navbar-nav" className="justify-content-between">

          {/* 左側選單 */}
          <Nav>
            <Nav.Link as={Link} to="/about">關於此網站</Nav.Link>
            <Nav.Link as={Link} to="/allCourse">全部課程</Nav.Link>
          </Nav>

          {/* 右側選單 */}
          <Nav>
            {!currentUser && (
              <>
                <Nav.Link as={Link} to="/register">註冊會員</Nav.Link>
                <Nav.Link as={Link} to="/login">會員登入</Nav.Link>
              </>
            )}
            {currentUser && (
              <>
                <Nav.Link as={Link} to="/profile">個人頁面</Nav.Link>
                <Nav.Link as={Link} to="/course">你的課程</Nav.Link>
                <Nav.Link onClick={handleLogout}>登出</Nav.Link>
              </>
            )}
          </Nav>

        </Navbar.Collapse>

      </Container>
    </Navbar>
  );
};

export default NavComponent;
