// 複貼chaosOK

import React from "react";
import { useNavigate } from "react-router-dom";
import homepage2 from "./img/homepage2.svg";
import homepage3 from "./img/homepage3.svg";
import homepage4 from "./img/homepage4.svg";
import homepage1 from "./img/homepage1.svg";
import homepage5 from "./img/homepage5.svg";
import homepage6 from "./img/homepage6.svg";
import "./style/style.css";

const HomeComponent = () => {
  const navigate = useNavigate();

  //導向所有課程
  const handleAddCourse = () => {
    window.alert("一起來看看有哪些課程吧!");
    navigate("/allCourse");
  };

  //導向張貼課程
  const handlePost = () => {
    window.alert("開始來上架新課程吧!");
    navigate("/postCourse");
  };

  //這是首頁
  return (
    <main>
      <div className="container py-4">
        <div className="p-5 mb-4 bg-primary-custom rounded-3">
          <div className="container-fluid py-5 text-center">
            <div className="col-md-12 d-flex align-items-center justify-content-center flex-wrap gap-3">
              <img
                src={homepage1}
                alt="good"
                style={{
                  height: "12vw",
                  width: "12vw",
                  filter: "drop-shadow(0 6px 8px rgba(0, 0, 0, 0.25))",
                }}
              />
              <img
                src={homepage2}
                alt="good"
                style={{
                  height: "12vw",
                  width: "12vw",
                  filter: "drop-shadow(0 6px 8px rgba(0, 0, 0, 0.25))",
                }}
              />

              <div className="text-center mx-3">
                <h1 className="display-4 fw-bold">下班上課趣</h1>
                <p className="h2">AfterWork Class</p>
              </div>

              <img
                src={homepage3}
                alt="good"
                style={{
                  height: "12vw",
                  width: "12vw",
                  filter: "drop-shadow(0 6px 8px rgba(0, 0, 0, 0.25))",
                }}
              />
              <img
                src={homepage4}
                alt="good"
                style={{
                  height: "12vw",
                  width: "12vw",
                  filter: "drop-shadow(0 6px 8px rgba(0, 0, 0, 0.25))",
                }}
              />
            </div>

            <br />
            <p className="col-md-12 fs-4">
              歡迎來到《下班上課趣》！ <br />
              這裡是一個讓上班族在下班後輕鬆預約喜歡課程的網站。
            </p>
            <p className="col-md-12 fs-4">
              使用者可依身份選擇登入為 <b>學員</b> 或 <b>講師</b>：
              <br />
              🎓 <b>學員</b> 可以瀏覽並預約下班後的各類興趣課程
              <br />
              🧑‍🏫 <b>講師</b> 可新增、編輯與刪除自己開設的課程內容
            </p>
            <hr />
            <p className="fw-bold fs-5">本網站為個人 Side Project</p>
            <p className="fs-5">前端採用 React 框架開發完成，後端使用 Node.js + Express.js。</p>
          </div>
        </div>

        <div className="row align-items-md-stretch">
          <div className="col-md-6">
            <div className="bg-primary-custom p-5 text-dark bg-opacity-50 border rounded-3">
              <h2>
                <img
                  src={homepage6}
                  alt="good"
                  style={{ height: "35%", width: "35%" }}
                ></img>
                我是學員
              </h2>
              <p>
                無論是瑜伽、攝影、烘焙還是外語練習
                <br />
                讓我們一起在下班後開啟充實又快樂的學習時光！
                <br />
              </p>
              <button
                className="btn btn-light"
                type="button"
                onClick={handleAddCourse}
              >
                立即選課
              </button>
            </div>
          </div>
          <div className="col-md-6">
            <div className="h-100 p-5 bg-primary-custom bg-opacity-10 border rounded-3">
              <h2>
                <img
                  src={homepage5}
                  alt="good"
                  style={{ height: "35%", width: "35%" }}
                ></img>
                我是講師
              </h2>
              <p>
                您可以在這裡發揮專業，上架課程！
                <br />
                推廣課程給更多人知道。
              </p>
              <button
                className="btn btn-light"
                type="button"
                onClick={handlePost}
              >
                開設課程
              </button>
            </div>
          </div>
        </div>

        <footer className="pt-3 mt-4 text-muted border-top ">
          &copy; 2025 WEI-YUAN CHANG Side Project
        </footer>
      </div>
    </main>
  );
};

export default HomeComponent;
