import React, { useState, useEffect } from "react";
// 如果使用者沒有登入就來到這個course component 的話 告訴她妳一定要先登入 並且給他一個button讓他到登陸的頁面去
import { useNavigate } from "react-router-dom";
// 使用者一進這個頁面就要用CourseService 得到他應該要看到的資料
import CourseService from "../services/course.service";

const CourseComponent = ({ currentUser, setCurrentUser }) => {
  const navigate = useNavigate();
  const handleTakeToLogin = () => {
    navigate("/login");
  };
  const [courseData, setCourseData] = useState(null);
  // 上面的courseData何時要從null 更新到其他值呢 用下面的useEffect

  useEffect(() => {
    let _id;
    // 如果有currentUser的話
    if (currentUser) {
      _id = currentUser.user._id;
      if (currentUser.user.role == "instructor") {
        // _id 就是instructor 的ID，CourseService.get會給一個Promise
        CourseService.get(_id)
          // .then 看到從伺服器拿到的data是什麼
          .then((data) => {
            setCourseData(data.data);
            console.log(data);
          })
          .catch((e) => {
            console.log(e);
          });
        // 如果腳色是student，用的CourseService 就不一樣
      } else if (currentUser.user.role == "student") {
        // 從Course.Service.js 拿到getEnrolledCourses這個Fn來用
        CourseService.getEnrolledCourses(_id)
          .then((data) => {
            // console.log(data);
            // 拿到的物件在這裡取名叫做data，你也可以取其他的
            setCourseData(data.data);
          })
          .catch((e) => {
            console.log(e);
          });
      }
    }
    // 後面的[]代表CourseComponent被render 的時候要執行他
  }, []);

  return (
    <div style={{ padding: "3rem" }}>
      {!currentUser && (
        <div>
          <p>您必須先登入才能看到課程。</p>
          <button
            className="btn btn-primary btn-lg"
            onClick={handleTakeToLogin}
          >
            回到登入頁面
          </button>
        </div>
      )}
      {/* 如果有currentUser，而且currentUser.user.role是"instructor"就出現這個頁面 */}
      {/* 有講師的課程頁面之後就可以把講師的課程資料抓出來 可以嘗試連結到server 的course model(course-route?) 任何講師所創建的課程都可從資料庫拿出來*/}
      {currentUser && currentUser.user.role == "instructor" && (
        <div>
          <h1>歡迎來到講師的課程頁面。</h1>
        </div>
      )}
      {currentUser && currentUser.user.role == "student" && (
        <div>
          <h1>歡迎來到學生的課程頁面。</h1>
        </div>
      )}
      {/* 如果有currentUser，也有courseData它不是null，courseData.length不是等於零 */}
      {currentUser && courseData && courseData.length != 0 && (
        <div style={{ display: "flex", flexWrap: "wrap" }}>
          {courseData.map((course) => {
            return (
              <div className="card" style={{ width: "18rem", margin: "1rem" }}>
                <div className="card-body">
                  <h5 className="card-title">課程名稱:{course.title}</h5>
                  <p style={{ margin: "0.5rem 0rem" }} className="card-text">
                    {course.description}
                  </p>
                  <p style={{ margin: "0.5rem 0rem" }}>
                    學生人數: {course.students.length}
                  </p>
                  <p style={{ margin: "0.5rem 0rem" }}>
                    課程價格: {course.price}
                  </p>
                </div>
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
};

export default CourseComponent;
